$(document).ready(function(){$("input[type=number]").each(function(){$(this).val(0)});$(".decrease").on("click",function(){var b=$(this).parent().children("input[type=number]")[0];var a=parseInt($(b).val());if(a>0){$(b).val(a-1)}});$(".increase").on("click",function(){var b=$(this).parent().children("input[type=number]")[0];var a=parseInt($(b).val());$(b).val(a+1)});$("#orderForm").on("submit",function(){const a='<input type="hidden" name="products[{0}]" value="{1}">';$("input[type=number]").each(function(){if($(this).val()>0){$("#products").append(format(a,[$(this).data("product-key"),$(this).val()]))}})})});function format(a,b){$.each(b,function(c,d){a=a.replace(new RegExp("\\{"+c+"\\}","g"),d)});return a};